# Update all JSON files
i18n_dir = Path("/Users/kiwinho/Proyectos/CodigoSH/web/static/i18n")

for json_file in i18n_dir.glob('*.json'):
    lang_code = json_file.stem
    
    with open(json_file, 'r', encoding='utf-8') as f:
        data = json.load(f)
    
    # Add translations for each page
    for page_name, translations in all_translations.items():
        if page_name not in data:
            data[page_name] = {}
        
        for key, lang_translations in translations.items():
            if lang_code in lang_translations:
                data[page_name][key] = lang_translations[lang_code]
    
    with open(json_file, 'w', encoding='utf-8') as f:
        json.dump(data, f, ensure_ascii=False, indent=4)
    
    print(f"✓ {json_file.name}: Added login, dashboard, and about translations")

print("\n✅ All pages ready for i18n!")
